import { useQuery } from "react-query";
import { getFakeData } from "../api/api";
import {Table} from 'antd'
import '../styles/MockDataTable.css';

const formatColumns = (data) => {
    console.log(data)
    if(!data){return []}
    return Object.keys(data[0]).filter(d=>d!=='userId').map((d,i)=>{
        return {
            title: d,
            dataIndex: d,
            key:i
        }
    })
}

export default function MockDataTable() {
  const numRecords = 3
  const { isLoading, error, data } = useQuery(
    [`helloWorld-${numRecords}`, {params: {numRecords}}], 
    getFakeData, 
    {keepPreviousData:true, enabled:true});

  let retElem = <div>Loading ...</div>

  if(error){
      retElem = <div>Error Fetching Data</div>
  }
  else if(!isLoading && data && data.length > 0){
      retElem = <Table 
        columns={formatColumns(data)} 
        dataSource={data} 
        size="small" 
        />
  }

  return (
    <div className="MockDataTable">
        {retElem}
    </div>
  );
}
